mapper.update:
CREATE SCHEMA vmb;
---------------------------------------------------------------------- -- TABLES ----------------------------------------------------------------------
CREATE TABLE vmb.board ( id serial NOT NULL, uri varchar(58) NOT NULL, title text NOT NULL, subtitle text, PRIMARY KEY(uri) ); CREATE INDEX ON vmb.board(uri);
----------------------------------------------------------------------
CREATE TABLE vmb.cite ( board int NOT NULL, post bigint NOT NULL, target_board int NOT NULL, target_post bigint NOT NULL, UNIQUE(board, post, target_board, target_post) ); CREATE INDEX ON vmb.cite(board, post); CREATE INDEX ON vmb.cite(target_board, target_post);
----------------------------------------------------------------------
CREATE TABLE vmb.mod ( id serial NOT NULL, username varchar(100) NOT NULL, password varchar(1000) NOT NULL, type smallint NOT NULL, boards text NOT NULL, PRIMARY KEY(id), UNIQUE(username) );
----------------------------------------------------------------------
CREATE TABLE vmb.post ( board int NOT NULL, id bigint NOT NULL, -- thread bigint DEFAULT NULL, subject varchar(100) DEFAULT NULL, email varchar(30) DEFAULT NULL, name varchar(35) DEFAULT NULL, trip varchar(15) DEFAULT NULL, capcode varchar(50) DEFAULT NULL, body text NOT NULL, body_nomarkup text, time bigint NOT NULL, bump bigint DEFAULT NULL, files text DEFAULT NULL, num_files smallint DEFAULT 0, filehash text, password varchar(20) DEFAULT NULL, ip varchar(39) NOT NULL, sticky boolean NOT NULL, locked boolean NOT NULL, cycle boolean NOT NULL, sage boolean NOT NULL, embed text, slug varchar(256) DEFAULT NULL ) PARTITION BY LIST(board);
----------------------------------------------------------------------
CREATE TABLE vmb.resource ( board int NOT NULL, post bigint NOT NULL, filename varchar(20) NOT NULL, UNIQUE(board, post, filename) ); CREATE INDEX ON vmb.resource(board, post); CREATE INDEX ON vmb.resource(filename);
----------------------------------------------------------------------
CREATE TABLE vmb.version ( version varchar(20) NOT NULL, UNIQUE(version) ); INSERT INTO vmb.version (version) VALUES (#{version});
